import Api from './Api';

class ChatApi {
  getAllPosts() {
    return Api.get('/chat');
  }

  getPostById(id) {
    return Api.get('/chat/' + id);
  }

  createPost(post) {
    return Api.post('/chat', chat);
  }

  updatePost(post) {
    return Api.put('/chat', chat);
  }

  deletePost(id) {
    return Api.delete('/chat/' + id);
  }
}

export default new ChatApi();
